<script>

$(document).ready(function(){	

	$("#modalfrm").validate({

		rules: {

			"mname":{required:true,minlength:4},

			"mphone":{required:true,digits:true},

			"memail":{required:true,email:true}

		}

	});

	$("#modalbtn-submit").click(function() {

		 if($("#modalfrm").valid()) {

			$('#modalfrm').submit(function(e){

				e.preventDefault();

				$.ajax({

				type: 'post',

				url: 'https://carajput.com/services/includes/modalsend-mail.php',

				data: "mname=" + $('#mname').val() + "&mphone=" + $('#mphone').val() + "&memail=" + $('#memail').val() + "&packName=" + $('#packName').val() + "&enquiry_page_url=" + $('#modal_enquiry_page_url').val(),

				success: function () {

				document.getElementById("status_message").innerHTML="Thanks! Your request submitted successfully!";

				document.getElementById("modalfrm").reset();

				}

			});

		 });

		 }

	});	

});

$(document).on("click", ".openModal", function () {

     var packName = $(this).data('id');

     $("#packName").val(packName);

     // As pointed out in comments, 

     // it is superfluous to have to manually call the modal.

     // $('#addBookDialog').modal('show');

});

$(document).on("click", ".close", function () {

	document.getElementById("status_message").reset();	

});



//Recaptcha part for 3 blocks



var onloadCallback = function() {

	var recaptchas = document.querySelectorAll('div[class=g-recaptcha]');



	for( i = 0; i < recaptchas.length; i++) {

		grecaptcha.render( recaptchas[i].id, {

		'sitekey' : '6Ley-BAUAAAAADaf415LFLI6RDuVJLzOAzmNBPIC',

		});

	}

}

document.getElementById("modalbtn-submit").disabled = true;

function enableMsBtn(){

	document.getElementById("modalbtn-submit").disabled = false;

}

</script>

